INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3142, 1, 0, '%s listens to $n\'s report...', 16, 0, 100, 0, 0, 0, 981, 0, 'Orgnil Soulscar');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3142, 2, 0, 'What??  The Burning Blade is spreading!  We must investigate!', 12, 1, 100, 5, 0, 0, 982, 0, 'Orgnil Soulscar');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3142, 3, 0, '%s smashes the dead, twisted claw.', 16, 0, 100, 36, 0, 0, 984, 0, 'Orgnil Soulscar');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 2, 0, 'Now I feel better. Let\'s get back to Ratchet. Come on, $n.', 12, 0, 100, 0, 0, 0, 1067, 0, 'gilthares SAY_GIL_PROCEED');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 3, 0, 'Looks like the Southsea Freebooters are heavily entrenched on the coast. This could get rough.', 12, 0, 100, 0, 0, 0, 1068, 0, 'gilthares SAY_GIL_FREEBOOTERS');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 4, 0, 'Help! $n attacking!', 12, 0, 100, 0, 0, 0, 1074, 0, 'gilthares SAY_GIL_AGGRO_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 4, 1, '$n heading this way fast! Time for revenge!', 12, 0, 100, 0, 0, 0, 1075, 0, 'gilthares SAY_GIL_AGGRO_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 4, 2, '$n coming right at us!', 12, 0, 100, 0, 0, 0, 1072, 0, 'gilthares SAY_GIL_AGGRO_3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 4, 3, 'Get this $n off of me!', 12, 0, 100, 0, 0, 0, 1073, 0, 'gilthares SAY_GIL_AGGRO_4');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 5, 0, 'Almost back to Ratchet! Let\'s keep up the pace...', 12, 0, 100, 0, 0, 0, 1069, 0, 'gilthares SAY_GIL_ALMOST');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 6, 0, 'Ah, the sweet salt air of Ratchet.', 12, 0, 100, 0, 0, 0, 1070, 0, 'gilthares SAY_GIL_SWEET');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3465, 7, 0, 'Captain Brightsun, $n here has freed me! $n, I am certain the Captain will reward your bravery.', 12, 0, 100, 0, 0, 0, 1071, 0, 'gilthares SAY_GIL_FREED');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3504, 0, 3, 'Why are we goin\' this way?', 12, 0, 100, 0, 0, 0, 1093, 0, 'Gil');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3504, 1, 3, 'I need to pee.', 12, 0, 100, 0, 0, 0, 1095, 0, 'Gil');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3520, 1, 0, 'O\'course I\'m talkin to myself. Only way to get decent conversation in this city.', 12, 7, 100, 0, 0, 0, 1108, 0, 'Ol\' Emma');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3520, 1, 1, 'As if I don\'t have better things to do in my old age than carry buckets of water.', 12, 7, 100, 0, 0, 0, 1106, 0, 'Ol\' Emma');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3520, 1, 2, 'Where\'s the water, Emma? Get the water, Emma? If\'n it weren\'t fer me, that lot wouldn\'t know what water looks like.', 12, 7, 100, 0, 0, 0, 1105, 0, 'Ol\' Emma');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3520, 1, 3, 'Deja vu.  For a moment, I thought I was back home... before the plague...', 12, 0, 100, 0, 0, 0, 1112, 0, 'Ol\' Emma');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3616, 3, 0, '%s continues his meditations...', 16, 0, 100, 0, 0, 0, 1219, 0, 'Onu');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3627, 0, 4, 'Free drinks at the Blue Recluse!', 12, 7, 100, 0, 0, 0, 1193, 0, 'Erich Lohan');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3627, 0, 5, 'Feeling blue? Come on down to the Blue Recluse for a good time!', 12, 7, 100, 0, 0, 0, 1199, 0, 'Erich Lohan');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3627, 0, 6, 'Head on over to the Blue Recluse. Where everybody knows your name!', 12, 7, 100, 0, 0, 0, 1194, 0, 'Erich Lohan');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3627, 0, 7, 'Ask me how to get a free drink at the Blue Recluse!', 12, 7, 100, 0, 0, 0, 1192, 0, 'Erich Lohan');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3692, 4, 2, 'We shall earn our deaths at the very least!', 12, 7, 100, 0, 0, 0, 1252, 0, 'Volcor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3692, 4, 3, 'Don\'t give up! Fight to the death!', 12, 7, 100, 0, 0, 0, 1253, 0, 'Volcor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3692, 4, 1, 'We can overcome these foul creatures!', 12, 7, 100, 0, 0, 0, 1251, 0, 'Volcor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3692, 4, 0, 'Here they come!', 12, 7, 100, 0, 0, 0, 1250, 0, 'Volcor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3692, 5, 0, 'We made it, my friend. Remember to find Terenthis and let him know we\'re safe. Thank you again.', 12, 7, 100, 0, 0, 0, 1243, 0, 'Volcor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3946, 3, 0, 'I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost.', 12, 7, 100, 0, 0, 0, 1359, 0, 'Velinde Starsong');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (3946, 4, 0, 'This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed?', 12, 7, 100, 0, 0, 0, 1360, 0, 'Velinde Starsong');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4063, 0, 3, 'Arent you a little short for your kind?', 12, 0, 100, 0, 0, 0, 809, 0, 'Feeboz');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4063, 0, 4, 'My life means nothing!  Our sacred task is all!', 12, 0, 100, 0, 0, 0, 810, 0, 'Feeboz');
