INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4063, 0, 6, 'Stand firm, brothers.  And dont worry!  Size is on our side!', 12, 0, 100, 0, 0, 0, 812, 0, 'Feeboz');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4063, 0, 7, 'Take that!  The Drywhiskers will prevail!', 12, 0, 100, 0, 0, 0, 814, 0, 'Feeboz');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (4063, 1, 0, 'May I bask in your shadow forever!', 12, 0, 100, 0, 0, 0, 799, 0, 'Feeboz');
