INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6176, 3, 0, 'I don\'t think it\'s strong enough.', 12, 0, 100, 0, 0, 0, 10865, 0, 'Bath\'rah the Windwatcher');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 1, 0, 'Ok, let\'s get this orb made...', 12, 0, 100, 0, 0, 0, 2663, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 2, 0, 'All right.  It\'s made!', 12, 0, 100, 1, 0, 0, 2664, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 3, 0, 'Stand back, and ... hold your breath.  I haven\'t done this in a while.', 12, 0, 100, 1, 0, 0, 2708, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 4, 0, 'It worked!  Amazing!  I guess I haven\'t lost my touch...', 12, 0, 100, 4, 0, 0, 2709, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 5, 0, 'Good luck out there, $n.', 12, 0, 100, 0, 0, 0, 2624, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 6, 0, 'I just have one thing to check...', 12, 0, 100, 0, 0, 0, 2656, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 7, 0, 'That\'s it!  It worked!', 12, 0, 100, 4, 0, 0, 2657, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 8, 0, 'Thanks for your patience, $n.', 12, 0, 100, 2, 0, 0, 2658, 0, 'Tabetha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (6546, 9, 0, 'I\'ll just keep this here until we need it...	', 12, 0, 100, 1, 0, 0, 2659, 0, 'Tabetha');
