INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8256, 2, 0, 'I\'ve been saving this spot for something special. I don\'t think it gets any more special than this, $n.', 12, 0, 100, 25, 0, 0, 4306, 0, 'Curator Thorius');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8256, 3, 0, 'There we go. All done!', 12, 0, 100, 0, 0, 0, 4307, 0, 'Curator Thorius');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8516, 0, 0, 'All right, stay close.  These fiends will jump right out of the shadows at you if you let your guard down.', 12, 0, 100, 0, 0, 0, 4493, 0, 'Belnistrasz SAY_QUEST_ACCEPTED');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (8516, 7, 0, 'Watch out for the $n!', 12, 0, 100, 0, 0, 0, 9006, 0, 'Belnistrasz SAY_WATCH_OUT');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 2, 0, '$n, thats it! I have finally figured it out!', 12, 0, 100, 5, 0, 0, 5289, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 1, 0, 'Blue plus red... I predict...', 12, 0, 100, 0, 0, 0, 5288, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 3, 0, 'Ahh... My experiments never seem to work out right. Maybe I should go back downstream.', 12, 0, 100, 0, 0, 0, 4843, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 4, 0, '%s begins to combine different colored crystals together.', 16, 0, 100, 0, 0, 0, 4846, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 5, 0, 'I think...I think it\'s working!', 12, 0, 100, 0, 0, 0, 4847, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 6, 0, 'OW! That\'s not right!', 12, 0, 100, 0, 0, 0, 4852, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 7, 0, 'Oh well... Maybe next time.', 12, 0, 100, 0, 0, 0, 4853, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 0, 'I know it\'ll work this time...', 12, 0, 100, 1, 0, 0, 4839, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 1, 'This will be my best experiment yet!', 12, 0, 100, 1, 0, 0, 4840, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 8, 2, 'I know it\'ll work this time... My last 13 tries were just practice!', 12, 0, 100, 1, 0, 0, 4842, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (9117, 9, 0, 'Now, if I add the yellow ones...', 12, 0, 100, 0, 0, 0, 4841, 0, 'J.D. Collie');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10262, 0, 3, 'They all go to the Spire but none ever come back... just like Bijou.', 12, 0, 100, 0, 0, 0, 5544, 0, 'Opus - Random Say');
