INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10262, 0, 6, 'Bijou lost! SQUAWK! *cough', 12, 0, 100, 0, 0, 0, 5541, 0, 'Opus - Random Say');
