INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10300, 2, 1, 'Please, light this while I am channeling.', 12, 0, 100, 0, 0, 0, 7378, 0, 'Ranshalla SAY_REACH_TORCH_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10300, 2, 2, 'This is the place. Let\'s light it.', 12, 0, 100, 0, 0, 0, 7379, 0, 'Ranshalla SAY_REACH_TORCH_3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10300, 27, 0, 'Thank you for your help, $n. I wish you well in your adventures. ', 12, 0, 100, 0, 0, 0, 7434, 0, 'Ranshalla SAY_RANSHALLA_END_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10300, 28, 0, 'I want to stay here and reflect on what we have seen. Please see Erelas and tell him what we have learned.', 12, 0, 100, 0, 0, 0, 7435, 0, 'Ranshalla SAY_RANSHALLA_END_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10300, 29, 0, '%s begins chanting a strange spell...', 16, 0, 100, 0, 0, 0, 7467, 0, 'Ranshalla EMOTE_CHANT_SPELL');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (10799, 0, 3, '%s whimpers.', 16, 0, 100, 0, 0, 0, 6101, 0, 'Warosh');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 0, 'Killing you be easy.', 12, 0, 100, 0, 0, 0, 1909, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 1, 'Troll mojo da strongest mojo!', 12, 0, 100, 0, 0, 0, 10437, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 2, 'Your skull gonna decorate our ritual altars!', 12, 0, 100, 0, 0, 0, 10436, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 3, 'I gonna make you into mojo!', 12, 0, 100, 0, 0, 0, 10435, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 4, 'Feel da big bad voodoo!', 12, 0, 100, 0, 0, 0, 10438, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 5, 'My weapon be thirsty!', 12, 0, 100, 0, 0, 0, 1908, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11350, 1, 6, 'You be dead soon!', 12, 0, 100, 0, 0, 0, 10434, 0, 'Part of the ZulGurub on aggro lines');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11440, 0, 3, 'Raaar!!! Me smash $r!', 12, 0, 100, 0, 0, 0, 1927, 0, 'Gordok Enforcer Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11582, 0, 3, 'Note the weak binding structure of this one. Be sure to finish your incantations or this is what you will end up with.', 12, 0, 100, 0, 0, 0, 5951, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11582, 0, 4, 'Wow, this one is just plain useless. Let me try again.', 12, 0, 100, 0, 0, 0, 5953, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11711, 4, 0, 'Please speak with Sentinel Onaeya at Maestra\'s Post in Ashenvale, she will see to it that you are properly rewarded for your bravery this day.', 12, 0, 100, 0, 0, 0, 7328, 0, 'Sentinel Aynasha');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (11980, 3, 0, 'Lord Illidan, bless me with the power of the flight!', 14, 0, 100, 0, 0, 0, 20126, 0, 'Zuluhed the Whacked');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 16, 0, 'Through the gem, Elune channeled her infinite wisdom...', 12, 0, 100, 0, 0, 0, 7415, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_11');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 17, 0, 'Realizing that the gem needed to be protected, we turned to the goddess herself.', 12, 0, 100, 0, 0, 0, 7417, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_12');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 18, 0, 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', 12, 0, 100, 0, 0, 0, 7418, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_13');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 19, 0, 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.\n', 12, 0, 100, 0, 0, 0, 7419, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_14');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 21, 0, 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...\n', 12, 0, 100, 0, 0, 0, 7422, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_16');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 22, 0, 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.\n', 12, 0, 100, 0, 0, 0, 7424, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_17');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 23, 0, 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', 12, 0, 100, 0, 0, 0, 7427, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_18');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 24, 0, 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', 12, 0, 100, 0, 0, 0, 7428, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_19');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 25, 0, 'Please, remember what we have shown you...', 12, 0, 100, 0, 0, 0, 7431, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_20');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12116, 26, 0, 'Farewell.', 12, 0, 100, 0, 0, 0, 33195, 0, 'Priestess of Elune SAY_PRIESTESS_ALTAR_21');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (12152, 20, 0, 'Anu\'dorini Talah, Ru shallora enudoril.', 12, 0, 100, 0, 0, 0, 0, 0, 'Voice of Elune SAY_VOICE_ALTAR_15');
