INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 3, 0, 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!\n', 14, 0, 100, 0, 0, 8611, 11437, 0, 'rajaxx SAY_WAVE6');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 4, 0, 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!\n', 14, 0, 100, 0, 0, 8607, 11436, 0, 'rajaxx SAY_WAVE7');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 5, 0, 'Impudent fool! I will kill you myself!', 14, 0, 100, 0, 0, 8609, 11435, 0, 'rajaxx SAY_INTRO');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 6, 0, 'Attack and make them pay dearly!', 14, 0, 100, 0, 0, 8603, 0, 0, 'rajaxx SAY_UNK1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 7, 0, 'Crush them! Drive them out!', 14, 0, 100, 0, 0, 8605, 0, 0, 'rajaxx SAY_UNK2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 8, 0, 'Do not hesitate! Destroy them!', 14, 0, 100, 0, 0, 8606, 0, 0, 'rajaxx SAY_UNK3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 9, 0, 'Warriors, Captains, continue the fight! ', 14, 0, 100, 0, 0, 8613, 11434, 0, 'rajaxx SAY_UNK4');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 10, 0, 'You are not worth my time, $n!', 14, 0, 100, 0, 0, 8614, 11067, 0, 'rajaxx SAY_DEAGGRO');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 11, 0, 'Breathe your last!', 14, 0, 100, 0, 0, 8604, 11433, 0, 'rajaxx SAY_KILLS_ANDOROV');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15341, 12, 0, 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will cease.', 14, 0, 100, 0, 0, 0, 11429, 0, 'rajaxx SAY_COMPLETE_QUEST');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15551, 1, 1, 'You are not welcome here!', 12, 0, 100, 0, 0, 0, 13774, 0, 'Spectral Stable Hand - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15551, 1, 0, 'Intruder!', 12, 0, 100, 0, 0, 0, 13773, 0, 'Spectral Stable Hand - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15644, 0, 2, 'More, more, more!  I must feed soon.', 12, 10, 100, 0, 0, 0, 12150, 0, 'Wretched Urchin');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (15645, 0, 2, 'More, more, more!  I must feed soon.', 12, 10, 100, 0, 0, 0, 12150, 0, 'Wretched Thug');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 5, 'They should have listened to me.  Then Id receive all of the accolades that are my due!', 12, 0, 100, 0, 0, 0, 12540, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 3, 'Im sure to receive a commendation upon our return. We may have suffered a few losses, but overall I think we did quite nicely, dont you, Valanna?', 12, 0, 100, 0, 0, 0, 12545, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 4, 'Did I not yell at them? Pull back! Pull back and regroup with suppressing fire!!', 12, 0, 100, 0, 0, 0, 12541, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 2, 'I tell you now, when we get back to Farstrider Enclave there will be a full accounting of your actions, Ranger Valanna! You alone were responsible for our losses and Im certain that the captain will see it my way.', 12, 0, 100, 0, 0, 0, 12543, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16217, 0, 1, 'This isnt Silvermoon City! Where are we? Oh, I was confused there for a moment.', 12, 0, 100, 0, 0, 0, 12542, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 4, 'Would you please shut up?!', 12, 0, 100, 0, 0, 0, 22240, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 5, 'With all due respect, sir, stick it!', 12, 0, 100, 0, 0, 0, 22243, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 2, 'Uh huh.  Whatever you say.', 12, 0, 100, 0, 0, 0, 22238, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 3, 'Idiot.', 12, 0, 100, 0, 0, 0, 22239, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16219, 0, 1, 'Remind me once again why it is that I dont just leave?', 12, 0, 100, 0, 0, 0, 22241, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16406, 0, 2, 'I mustn\'t be afraid....', 12, 0, 100, 0, 0, 0, 13849, 0, 'Phantom Attendant - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16406, 1, 0, 'You\'ll ruin everything!', 12, 0, 100, 0, 0, 0, 13844, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16406, 1, 1, 'Off with you!', 12, 0, 100, 0, 0, 0, 13845, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16406, 1, 2, 'We do not wish to be disturbed!', 12, 0, 100, 0, 0, 0, 13846, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16407, 1, 0, 'What is this?', 12, 0, 100, 0, 0, 0, 13820, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16407, 1, 1, 'Invaders in the tower!', 12, 0, 100, 0, 0, 0, 13821, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16407, 1, 2, 'Stop them!', 12, 0, 100, 0, 0, 0, 13822, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 0, 1, 'My time is done.', 12, 0, 100, 0, 0, 0, 13857, 0, 'Phantom Valet - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 0, 2, 'No, not now! Not like this!', 12, 0, 100, 0, 0, 0, 13858, 0, 'Phantom Valet - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 1, 0, 'Hey! Where do you think you are going, sneaking around like that?', 12, 0, 100, 0, 0, 0, 13398, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 1, 1, 'Excuse me, are you trying to sneak by without an invitation?', 12, 0, 100, 0, 0, 0, 13399, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 1, 2, 'Begone, outsider!', 12, 0, 100, 0, 0, 0, 13851, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 1, 3, 'You\'ll not ruin the celebration!', 12, 0, 100, 0, 0, 0, 13852, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16408, 1, 4, 'We are besieged!', 12, 0, 100, 0, 0, 0, 13853, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16409, 0, 1, 'It\'s not my time, not yet!', 12, 0, 100, 0, 0, 0, 13842, 0, 'Phantom Guest - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16409, 0, 2, 'All good things must come to an end.', 12, 0, 100, 0, 0, 0, 13843, 0, 'Phantom Guest - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16409, 1, 0, 'How dare you interfere?!', 12, 0, 100, 0, 0, 0, 13838, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16409, 1, 1, 'You do not belong here!', 12, 0, 100, 0, 0, 0, 13839, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16409, 1, 2, 'Leave us alone! Let us be!', 12, 0, 100, 0, 0, 0, 13840, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16414, 0, 3, 'How dare you spill the master\'s wine!', 12, 0, 100, 0, 0, 0, 13435, 0, 'Ghostly Steward - On Death');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16414, 1, 1, 'Meddling fools! You will pay with your lives!', 12, 0, 100, 0, 0, 0, 13877, 0, 'Ghostly Steward - On Aggro');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (16414, 1, 2, 'You\'ll never make it out alive....', 12, 0, 100, 0, 0, 0, 13879, 0, 'Ghostly Steward - On Aggro');
