INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17968, 4, 1, 'Your soul will languish for eternity.', 14, 0, 100, 0, 0, 10991, 18264, 0, 'Archimonde - SAY_SLAY12');
