INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17976, 1, 1, 'Mission accomplished!', 14, 0, 100, 0, 0, 11073, 19938, 0, 'Commander Sarannis - On Player Death Say');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17976, 6, 0, 'I have not yet... begun to...', 14, 0, 100, 0, 0, 11079, 19942, 0, 'Commander Sarannis - On Death Say');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17978, 5, 1, 'Scream while you burn!', 14, 0, 100, 0, 0, 11211, 20018, 0, 'Thorngrin the Tender - Cast Hellfire Say');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 0, 0, 'There\'s no turning back now. Stay close and watch my back.', 12, 0, 100, 1, 0, 0, 14648, 0, 'npc_demolitionist_legoso SAY_LEGOSO_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 2, 0, 'Cover me!', 12, 0, 100, 5, 0, 0, 14650, 0, 'npc_demolitionist_legoso SAY_LEGOSO_3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 3, 0, 'It won\'t be much longer. Just keep them off me while I work.', 12, 0, 100, 69, 0, 0, 14651, 0, 'npc_demolitionist_legoso SAY_LEGOSO_4');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 4, 0, 'That\'ll do it! Quickly, take cover!', 12, 0, 100, 5, 0, 0, 14652, 0, 'npc_demolitionist_legoso SAY_LEGOSO_5');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 5, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_6');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 6, 0, '2...', 12, 0, 100, 0, 0, 0, 14654, 0, 'npc_demolitionist_legoso SAY_LEGOSO_7');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 7, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_8');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 8, 0, 'Don\'t get too excited, hero, that was the easy part. The challenge lies ahead! Let\'s go.', 12, 0, 100, 1, 0, 0, 14656, 0, 'npc_demolitionist_legoso SAY_LEGOSO_9');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 9, 0, 'What in the Nether is that?!?!', 12, 0, 100, 5, 0, 0, 14657, 0, 'npc_demolitionist_legoso SAY_LEGOSO_10');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 10, 0, 'Be ready for anything.', 12, 0, 100, 1, 0, 0, 14658, 0, 'npc_demolitionist_legoso SAY_LEGOSO_11');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 11, 0, 'Blessed Light! She\'s siphoning energy right out of the Vector Coil!', 12, 0, 100, 53, 0, 0, 14659, 0, 'npc_demolitionist_legoso SAY_LEGOSO_12');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 12, 0, 'Cover me, we have to do this quickly. Once I blow the support on this side, it will disrupt the energy beams and she\'ll break out! I doubt very much that she\'ll be happy to see us.', 12, 0, 100, 1, 0, 0, 14660, 0, 'npc_demolitionist_legoso SAY_LEGOSO_13');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 13, 0, 'I\'ve almost got it! Just a little more time...', 12, 0, 100, 0, 0, 0, 14661, 0, 'npc_demolitionist_legoso SAY_LEGOSO_14');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 14, 0, 'Take cover and be ready for the fight of your life!', 12, 0, 100, 5, 0, 0, 14662, 0, 'npc_demolitionist_legoso SAY_LEGOSO_15');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 15, 0, '3...', 12, 0, 100, 0, 0, 0, 14653, 0, 'npc_demolitionist_legoso SAY_LEGOSO_16');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 16, 0, '2...', 12, 0, 100, 0, 0, 0, 14654, 0, 'npc_demolitionist_legoso SAY_LEGOSO_17');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 17, 0, '1...', 12, 0, 100, 0, 0, 0, 14655, 0, 'npc_demolitionist_legoso SAY_LEGOSO_18');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 18, 0, 'Holy mother of O\'ros!', 12, 0, 100, 5, 0, 0, 14663, 0, 'npc_demolitionist_legoso SAY_LEGOSO_19');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 19, 0, 'I... I can\'t believe it\'s over. You did it! You\'ve destoyed the blood elves and their leader!', 12, 0, 100, 1, 0, 0, 14664, 0, 'npc_demolitionist_legoso SAY_LEGOSO_20');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (17982, 20, 0, 'Get back to Blood Watch. I\'ll see you there...', 12, 0, 100, 1, 0, 0, 14665, 0, 'npc_demolitionist_legoso SAY_LEGOSO_21');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18103, 0, 2, 'Happiness is mandatory, citizen.', 12, 0, 100, 0, 0, 0, 14782, 0, 'Arcane Guardian - Random Talk');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18103, 0, 3, 'Remain strong. Kael\'thas will - error - Lor\'themar will lead you to power and glory!', 12, 0, 100, 0, 0, 0, 14784, 0, 'Arcane Guardian - Random Talk');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18103, 0, 4, 'Do not disturb the serenity of the city. Peace must be upheld.', 12, 0, 100, 0, 0, 0, 14785, 0, 'Arcane Guardian - Random Talk');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18351, 1, 2, 'OK, OK! Lump give up!', 12, 0, 100, 0, 0, 0, 15282, 0, 'Lump');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18407, 1, 0, 'That\'s the best reward we\'re going to be able to offer. Hopefully someone accepts the mission.', 12, 1, 100, 1, 0, 0, 15372, 0, 'Warden Bullrok');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18428, 0, 3, 'Spirits watch over you, $r!', 12, 0, 100, 0, 0, 0, 15390, 0, 'Mag\'har Prisoner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18428, 0, 4, 'Thank you, $r! Your service to the Mag\'har will not be forgotten.', 12, 0, 100, 0, 0, 0, 15387, 0, 'Mag\'har Prisoner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18482, 1, 0, 'I\'m leaving now!', 12, 0, 100, 1, 0, 0, 15491, 0, 'Empoor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18554, 4, 0, 'I can assure you that we will not fail, my master.  I am personally overseeing every aspect of the construction, and I hold the final codes, myself.  Within a day\'s time, I will have the bomb detonated on those nearby pests.', 12, 0, 100, 2, 0, 0, 19164, 0, 'Sharth Voldoun');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18554, 3, 0, 'You are satisfied?', 12, 0, 100, 6, 0, 0, 19160, 0, 'Sharth Voldoun');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18554, 1, 0, 'My master, we are honored by your presence.  How may I serve thee?', 12, 0, 100, 0, 0, 0, 19156, 0, 'Sharth Voldoun');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18554, 2, 0, 'All goes exceedingly well, my lord.  Testing of the smaller prototype at the Cenarion Thicket was a complete success.  The second bomb is being ritually fueled in the courtyard below even as we speak.  And, I\'\'ve sent a courier to Tuurem to bring the rest of the parts to us here.', 12, 0, 100, 1, 0, 0, 19159, 0, 'Sharth Voldoun');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 11, 'What is this trickery? I... ARGH...', 12, 0, 100, 0, 0, 0, 16840, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 10, 'What is happening to me?', 12, 0, 100, 0, 0, 0, 15703, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 9, 'The pain is unbearable.', 12, 0, 100, 0, 0, 0, 15705, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 8, 'The cold embrace awaits...', 12, 0, 100, 0, 0, 0, 16837, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 7, 'It is dying. It is dying. It is dying.', 12, 0, 100, 0, 0, 0, 15707, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 6, 'Cannot control... It draws me in... The void grows...', 12, 0, 100, 0, 0, 0, 15710, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 5, 'The spirits call... I am consumed.', 12, 0, 100, 0, 0, 0, 15704, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 12, 'What will become of us when it falls?', 12, 0, 100, 0, 0, 0, 15709, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 13, 'What... What have I become? Arrrgghhh...', 12, 0, 100, 0, 0, 0, 16835, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18688, 0, 14, 'Thank you...', 12, 0, 100, 0, 0, 0, 16836, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18810, 0, 1, 'If you don\'t eat your fish, you can\'t have any pudding!', 12, 7, 100, 5, 0, 0, 15942, 0, 'Otonambusi');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18929, 5, 0, 'Cakes, pastries, even bread - anything but cheese!', 12, 0, 100, 0, 0, 0, 16098, 0, 'Kyrenna');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18929, 4, 0, 'Why couldn\'t mother have chosen something else to dedicate herself to?', 12, 0, 100, 1, 0, 0, 16097, 0, 'Kyrenna');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (18929, 3, 0, 'The hours would pass so much more sweetly.', 12, 0, 100, 0, 0, 0, 16096, 0, 'Kyrenna');
