INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21315, 1, 0, 'Your world is at an end.', 12, 0, 100, 0, 0, 0, 19290, 0, 'Ruul the Darkener');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21319, 0, 1, 'Hah!  The Thunderspike is mine.  Die!', 12, 0, 100, 0, 0, 0, 18979, 0, 'Gor Grimgut');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21661, 1, 0, 'The end comes for you!', 12, 0, 100, 0, 0, 0, 16800, 0, 'Cabal Skirmisher');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21661, 1, 1, 'You will not escape us so easily!', 12, 0, 100, 0, 0, 0, 20165, 0, 'Cabal Skirmisher');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (21661, 1, 2, 'I shall be rewarded!', 12, 0, 100, 0, 0, 0, 16803, 0, 'Cabal Skirmisher');
