INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 2, 0, 'I knew! ', 12, 0, 100, 5, 0, 17385, 38203, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 3, 0, 'I sensed a part of him still alive! Trapped... struggling... Oh, Arthas!', 12, 0, 100, 396, 0, 17386, 38204, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 4, 0, 'Perhaps - perhaps he might someday remember what he once was.', 12, 0, 100, 396, 0, 17387, 38205, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38606, 5, 0, 'By the Light, may he at last find rest, free from the icy grip of that terrible blade.', 12, 0, 100, 396, 0, 17388, 38206, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 0, 0, 'Oh, lad...', 12, 0, 100, 396, 0, 17421, 38171, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 1, 0, 'How I miss those endless days in Lordaeron...', 12, 0, 100, 396, 0, 17422, 38172, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 2, 0, '...sharpenin\' yer skill with this dull blade...', 12, 0, 100, 396, 0, 17423, 38173, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 3, 0, '...forgin\' ya into a weapon meant to withstand the demands of a great destiny.', 12, 0, 100, 396, 0, 17424, 38174, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 4, 0, 'Heh... Yeh sure put them skills ta use, didn\'t yeh, lad?', 12, 0, 100, 6, 0, 17425, 38175, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 5, 0, 'If only I\'da been able ta stop yeh that day, how different things mighta been.', 12, 0, 100, 396, 0, 17426, 38176, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 6, 0, 'If only I\'da never discovered that accursed blade...', 12, 0, 100, 396, 0, 17427, 38177, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38607, 7, 0, 'Farewell, Arthas... my brother.', 12, 0, 100, 396, 0, 17428, 38178, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 0, 0, 'Arthas...', 12, 0, 100, 396, 0, 17402, 38160, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 1, 0, 'Alas, hero of Azeroth, you give me a greater gift than you know.', 12, 0, 100, 396, 0, 17403, 38161, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 2, 0, 'Long have I struggled to forgive the prince for his terrible transgressions.', 12, 0, 100, 396, 0, 17404, 38162, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 3, 0, 'My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light.', 12, 0, 100, 396, 0, 17405, 38163, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 4, 0, 'I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light...', 12, 0, 100, 396, 0, 17406, 38165, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 5, 0, 'Eager to defend his people, no matter the cost.', 12, 0, 100, 396, 0, 17407, 38166, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 6, 0, 'It is this memory of Arthas that I choose to keep in my heart.', 12, 0, 100, 396, 0, 17408, 38167, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 7, 0, 'I shall always be in your debt, friend.', 12, 0, 100, 396, 0, 17409, 38168, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38608, 8, 0, 'Thank you.', 12, 0, 100, 0, 0, 17410, 38169, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 0, 0, 'So, it is done.', 12, 0, 100, 396, 0, 17376, 38179, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 1, 0, 'I had not dared to trust my senses - too many times has the Lich King made me to be a fool.', 12, 0, 100, 396, 0, 17377, 38180, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 2, 0, 'Finally, he has been made to pay for the atrocities he imposed upon my people.', 12, 0, 100, 396, 0, 17378, 38181, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 3, 0, 'May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride.', 12, 0, 100, 396, 0, 17379, 38183, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 4, 0, 'But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils?', 12, 0, 100, 6, 0, 17380, 38184, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 5, 0, 'Leave me. ', 12, 0, 100, 396, 0, 17381, 38185, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38609, 6, 0, 'I have much to ponder.', 12, 0, 100, 396, 0, 17382, 38186, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 0, 0, 'Darion, my son.', 12, 0, 100, 396, 0, 17414, 38152, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 1, 0, 'At last, I am able to lay my eyes upon you again.', 12, 0, 100, 396, 0, 17415, 38153, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 2, 0, 'The Lich King tormented me without end, Darion.', 12, 0, 100, 396, 0, 17416, 38154, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 3, 0, 'Endlessly, he sought to break my will, to force me to serve him, to bind me to his blade...', 12, 0, 100, 396, 0, 17417, 38155, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 4, 0, 'Finally, when events demanded his full attention, he left me.', 12, 0, 100, 396, 0, 17418, 38156, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 5, 0, 'The one memory, I clung to, Darion... The one thought that kept me from giving in...', 12, 0, 100, 396, 0, 17419, 38157, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38610, 6, 0, 'It was your sacrifice, my son, that again saved me from eternal peril.', 12, 0, 100, 396, 0, 17420, 38158, 0, 'Highlord Alexandros Mograine');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38867, 0, 0, 'All right, boss. You sure though? Just seems like a waste of good practice.', 12, 0, 100, 0, 0, 0, 1716, 0, 'Old Town Thug');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38867, 1, 0, '%s cracks his knuckles.', 16, 0, 100, 0, 0, 0, 1717, 0, 'Old Town Thug');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38995, 0, 0, 'We\'ll grant you a swift death, Arthas. More than can be said for the thousands you\'ve tortured and slain.', 14, 0, 0, 0, 0, 17390, 38109, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38995, 1, 0, 'So be it. Champions, attack!', 14, 0, 0, 0, 0, 17391, 38110, 0, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38995, 2, 0, 'LIGHT GRANT ME ONE FINAL BLESSING! GIVE ME THE STRENGTH... TO SHATTER THESE BONDS!', 14, 0, 0, 0, 0, 17392, 38111, 0, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (38995, 3, 0, 'No more, Arthas! No more lives will be consumed by your hatred!', 14, 0, 0, 0, 0, 17393, 38112, 0, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39217, 0, 0, 'You have come to bring Arthas to justice? To see the Lich King destroyed?', 14, 0, 0, 0, 0, 17394, 38114, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39217, 1, 0, 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity. ', 14, 0, 0, 0, 0, 17395, 38115, 0, 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 13, 0, 'Fantastic!', 12, 0, 100, 273, 0, 0, 39432, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 14, 0, ' However, the most important part of battle is how you celebrate a good hard earned victory!', 12, 0, 100, 396, 0, 0, 39384, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 12, 0, 'Everyone, cheer!', 12, 0, 100, 0, 0, 0, 39387, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 11, 0, 'Reward your fellow soldiers in battle with a resounding cheer.  Now, cheer at me when I tell you to!', 12, 0, 100, 1, 0, 0, 39428, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 9, 0, 'Wow, nice and scary! ', 12, 0, 100, 0, 0, 0, 39431, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 10, 0, 'Remember though, a key factor in winning any battle is positive reinforcement.', 12, 0, 100, 5, 0, 0, 39386, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 8, 0, 'Let me hear that battle roar!', 12, 0, 100, 5, 0, 0, 39395, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 7, 0, 'When I give the signal, let loose your greatest roar!', 12, 0, 100, 1, 0, 0, 39411, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 5, 0, 'Nice job!', 12, 0, 100, 273, 0, 0, 39430, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 6, 0, 'When you go into combat, it is important that you know how to intimidate the enemy with a deafening battle roar!', 12, 0, 100, 396, 0, 0, 39385, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 4, 0, 'Okay, recruits, salute!', 12, 0, 100, 66, 0, 0, 39394, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 3, 0, 'Show me that discipline with a proper salute when I say so!', 12, 0, 100, 5, 0, 0, 39413, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 2, 0, 'The first thing you need to learn is proper discipline.', 12, 0, 100, 1, 0, 0, 39383, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 1, 0, 'I am going to teach you all what it takes to be a proper soldier! ', 12, 0, 100, 5, 0, 0, 39404, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 0, 0, 'All right, you twisted mess of broken gears, let\'s get to work!', 12, 0, 100, 1, 0, 0, 39392, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 15, 0, 'Show me your best victory dance when I give the signal!', 12, 0, 100, 5, 0, 0, 39429, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 16, 0, 'Now, dance!', 12, 0, 100, 0, 0, 0, 39403, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 17, 0, 'Superb!', 12, 0, 100, 273, 0, 0, 39433, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39368, 18, 0, 'You might be the best set of recruits I\'ve ever seen!  Let\'s go through all of that again!', 12, 0, 100, 396, 0, 0, 39434, 0, 'Drill Sergeant Steamcrank');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 0, 0, 'They may take our lives, but they\'ll never take...', 12, 0, 100, 0, 0, 0, 39708, 0, 'High Tinker Mekkatorque to Milli Featherwhistle 1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 1, 0, '...our INNOVATION!', 12, 0, 100, 0, 0, 0, 39722, 0, 'High Tinker Mekkatorque to Milli Featherwhistle 2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 2, 0, 'What I want out of each and every one of you is a hard-target search of every refuelling station, residence, warehouse, farmhouse, henhouse, outhouse and doghouse in this area. ', 12, 0, 100, 0, 0, 0, 39709, 0, 'High Tinker Mekkatorque to Ozzie Togglevolt 1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 3, 0, 'Your fugitive\'s name is Mekgineer Thermaplugg.', 12, 0, 100, 0, 0, 0, 39723, 0, 'High Tinker Mekkatorque to Ozzie Togglevolt 2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 4, 0, 'Go get him.', 12, 0, 100, 0, 0, 0, 39724, 0, 'High Tinker Mekkatorque to Ozzie Togglevolt 3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 5, 0, 'We will not go quietly into the night! We will not vanish without a fight!', 12, 0, 100, 0, 0, 0, 39710, 0, 'High Tinker Mekkatorque to Tog Rustsprocket 1');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 6, 0, ' We\'re going to live on! We\'re going to survive! Today we celebrate...', 12, 0, 100, 0, 0, 0, 39725, 0, 'High Tinker Mekkatorque to Tog Rustsprocket 2');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39712, 7, 0, '...our Autonomy Day!', 12, 0, 100, 0, 0, 0, 39726, 0, 'High Tinker Mekkatorque to Tog Rustsprocket 3');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39746, 0, 0, 'Alexstrasza has chosen capable allies.... A pity that I must END YOU!', 14, 0, 100, 0, 0, 17512, 39781, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39746, 1, 0, 'You thought you stood a chance?', 14, 0, 50, 0, 0, 17513, 39782, 0, 'General Zarithrian');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39746, 1, 1, 'It\'s for the best.', 14, 0, 50, 0, 0, 17514, 39783, 0, 'General Zarithrian');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39746, 2, 0, 'Turn them to ash, minions!', 14, 0, 100, 0, 0, 17516, 39785, 0, 'General Zarithrian');
