INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 0, 0, 'Your power wanes, ancient one.... Soon you will join your friends.', 14, 0, 100, 0, 0, 17525, 40614, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 1, 0, 'Ah, the entertainment has arrived.', 14, 0, 100, 0, 0, 17520, 39935, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 2, 0, 'Baltharus leaves no survivors!', 14, 0, 100, 0, 0, 17521, 39937, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 2, 1, 'This world has enough heroes.', 14, 0, 100, 0, 0, 17522, 39938, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 3, 0, 'Twice the pain and half the fun.', 14, 0, 100, 0, 0, 17524, 39939, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39751, 4, 1, 'I... didn\'t see that coming....', 14, 0, 100, 0, 0, 17523, 39936, 0, 'Baltharus the Warborn');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 0, 0, 'Without pressure in both realms, %s begins to regenerate.', 41, 0, 100, 0, 0, 0, 40476, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 1, 0, 'Meddlesome insects! You are too late. The Ruby Sanctum is lost!', 14, 0, 100, 1, 0, 17499, 39942, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 2, 0, 'Your world teeters on the brink of annihilation. You will ALL bear witness to the coming of a new age of DESTRUCTION!', 14, 0, 100, 0, 0, 17500, 40064, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 3, 0, 'The heavens burn!', 14, 0, 100, 0, 0, 17505, 40068, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 4, 0, 'You will find only suffering within the realm of twilight! Enter if you dare!', 14, 0, 100, 0, 0, 17507, 40067, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 5, 0, 'Relish this victory, mortals, for it will be your last. This world will burn with the master\'s return!', 14, 0, 100, 0, 0, 17503, 40065, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 6, 0, 'Another \"hero\" falls.', 14, 0, 100, 0, 0, 17501, 40066, 0, 'Halion');
INSERT INTO `creature_text`(`CreatureID`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextId`, `TextRange`, `comment`) VALUES (39863, 7, 0, 'Not good enough....', 14, 0, 100, 0, 0, 17504, 41619, 0, 'Halion');
